import{_ as s,c as n,o as a,a as e}from"./app.437f86da.js";const m=JSON.parse('{"title":"Promise","description":"","frontmatter":{},"headers":[{"level":2,"title":"Promise \u7684\u542B\u4E49","slug":"promise-\u7684\u542B\u4E49","link":"#promise-\u7684\u542B\u4E49","children":[]},{"level":2,"title":"Promise \u7684\u7279\u70B9","slug":"promise-\u7684\u7279\u70B9","link":"#promise-\u7684\u7279\u70B9","children":[]},{"level":2,"title":"Promise \u7684\u7F3A\u70B9","slug":"promise-\u7684\u7F3A\u70B9","link":"#promise-\u7684\u7F3A\u70B9","children":[]},{"level":2,"title":"\u57FA\u672C\u7528\u6CD5","slug":"\u57FA\u672C\u7528\u6CD5","link":"#\u57FA\u672C\u7528\u6CD5","children":[]},{"level":2,"title":"Promise.then()","slug":"promise-then","link":"#promise-then","children":[]},{"level":2,"title":"Promise.catch()","slug":"promise-catch","link":"#promise-catch","children":[{"level":3,"title":"\u5EFA\u8BAE","slug":"\u5EFA\u8BAE","link":"#\u5EFA\u8BAE","children":[]}]},{"level":2,"title":"Promise.finally()","slug":"promise-finally","link":"#promise-finally","children":[]},{"level":2,"title":"Promise \u9759\u6001\u65B9\u6CD5","slug":"promise-\u9759\u6001\u65B9\u6CD5","link":"#promise-\u9759\u6001\u65B9\u6CD5","children":[{"level":3,"title":"Promise.all()","slug":"promise-all","link":"#promise-all","children":[]},{"level":3,"title":"Promise.race()","slug":"promise-race","link":"#promise-race","children":[]},{"level":3,"title":"Promise.resolve()","slug":"promise-resolve","link":"#promise-resolve","children":[]},{"level":3,"title":"Promise.reject()","slug":"promise-reject","link":"#promise-reject","children":[]}]},{"level":2,"title":"Promise \u5B9E\u8DF5","slug":"promise-\u5B9E\u8DF5","link":"#promise-\u5B9E\u8DF5","children":[{"level":3,"title":"\u56FE\u7247\u52A0\u8F7D","slug":"\u56FE\u7247\u52A0\u8F7D","link":"#\u56FE\u7247\u52A0\u8F7D","children":[]}]}],"relativePath":"articles/javascript/Promise.md"}'),l={name:"articles/javascript/Promise.md"},p=e(`<h1 id="promise" tabindex="-1">Promise <a class="header-anchor" href="#promise" aria-hidden="true">#</a></h1><h2 id="promise-\u7684\u542B\u4E49" tabindex="-1">Promise \u7684\u542B\u4E49 <a class="header-anchor" href="#promise-\u7684\u542B\u4E49" aria-hidden="true">#</a></h2><p>Promise \u7684\u542B\u4E49\u4E3A\u627F\u8BFA\uFF0C\u662F\u4E00\u79CD\u5F02\u6B65\u7F16\u7A0B\u7684\u89E3\u51B3\u65B9\u6848\uFF0C\u4E13\u95E8\u7528\u6765\u89E3\u51B3\u56DE\u8C03\u5D4C\u5957\u5BFC\u81F4\u591A\u5C42\u5730\u72F1\u7684\u95EE\u9898\u3002 \u7B80\u5355\u6765\u8BF4\uFF0C\u5B83\u5C31\u662F\u4E00\u4E2A\u5BB9\u5668\uFF0C\u4FDD\u5B58\u7740\u67D0\u4E2A\u672A\u6765\u624D\u4F1A\u7ED3\u675F\u7684\u4E8B\u4EF6\uFF1B</p><h2 id="promise-\u7684\u7279\u70B9" tabindex="-1">Promise \u7684\u7279\u70B9 <a class="header-anchor" href="#promise-\u7684\u7279\u70B9" aria-hidden="true">#</a></h2><ul><li><p>\u5BF9\u8C61\u7684\u72B6\u6001\u4E0D\u53D7\u5916\u754C\u5F71\u54CD\u3002Promise \u6709\u4E09\u79CD\u72B6\u6001\uFF0C<em>Pending</em>\uFF08\u8FDB\u884C\u4E2D\uFF09\u3001<em>Resolved</em>\uFF08\u5DF2\u5B8C\u6210\uFF09\u548C <em>Rejected</em>\uFF08\u5DF2\u5931\u8D25\uFF09\u3002\u53EA\u6709\u5F02\u6B65\u64CD\u4F5C\u7684\u7ED3\u679C\uFF0C\u53EF\u4EE5\u51B3\u5B9A\u5F53\u524D\u662F\u54EA\u4E00\u79CD\u72B6\u6001\uFF0C\u4EFB\u4F55\u5176\u4ED6\u64CD\u4F5C\u90FD\u65E0\u6CD5\u6539\u53D8\u8FD9\u4E2A\u72B6\u6001</p></li><li><p>\u4E00\u65E6\u72B6\u6001\u6539\u53D8\uFF0C\u5C31\u4E0D\u4F1A\u518D\u53D8\u3002Promise \u72B6\u6001\u6539\u53D8\u53EA\u6709\u4E24\u79CD\u53EF\u80FD\uFF0CPending-&gt;Resolved \u548C Pending-&gt;Rejected\u3002</p></li></ul><h2 id="promise-\u7684\u7F3A\u70B9" tabindex="-1">Promise \u7684\u7F3A\u70B9 <a class="header-anchor" href="#promise-\u7684\u7F3A\u70B9" aria-hidden="true">#</a></h2><ul><li>\u65E0\u6CD5\u53D6\u6D88 Promise\uFF0C\u4E00\u65E6\u65B0\u5EFA\u5C31\u4F1A\u7ACB\u5373\u6267\u884C\uFF0C\u65E0\u6CD5\u53D6\u6D88\u3002</li><li>\u5982\u679C\u4E0D\u8BBE\u7F6E\u56DE\u8C03\uFF0CPromise \u5185\u90E8\u7684\u9519\u8BEF\u4E0D\u4F1A\u53CD\u6620\u5230\u5916\u90E8\u3002</li></ul><h2 id="\u57FA\u672C\u7528\u6CD5" tabindex="-1">\u57FA\u672C\u7528\u6CD5 <a class="header-anchor" href="#\u57FA\u672C\u7528\u6CD5" aria-hidden="true">#</a></h2><p>\u521B\u9020\u4E00\u4E2A Promise \u7684\u5B9E\u4F8B</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">let Promise = new Promise(function(resolve, reject) {</span></span>
<span class="line"><span style="color:#A6ACCD;">  if (/* \u5F02\u6B65\u64CD\u4F5C\u6210\u529F */){</span></span>
<span class="line"><span style="color:#A6ACCD;">    resolve(value);</span></span>
<span class="line"><span style="color:#A6ACCD;">  } else {</span></span>
<span class="line"><span style="color:#A6ACCD;">    reject(error);</span></span>
<span class="line"><span style="color:#A6ACCD;">  }</span></span>
<span class="line"><span style="color:#A6ACCD;">});</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>Promise \u6784\u9020\u51FD\u6570\u63A5\u6536\u4E00\u4E2A\u51FD\u6570\u4F5C\u4E3A\u53C2\u6570\uFF0C\u51FD\u6570\u7684\u4E24\u4E2A\u53C2\u6570\u4E3A resolve \u548C reject\uFF0C\u4E3A\u5185\u90E8\u5F15\u64CE\u63D0\u4F9B\u3002</p><p>resolve \u51FD\u6570\u5728\u5F02\u6B65\u64CD\u4F5C\u6210\u529F\u65F6\u8C03\u7528\uFF0C\u5E76\u5C06\u7ED3\u679C\u4F20\u9012\u51FA\u53BB\uFF0C\u5BF9\u5E94\u7684 reject \u51FD\u6570\u5219\u662F\u5728\u64CD\u4F5C\u5931\u8D25\u65F6\u8C03\u7528\uFF0C\u5E76\u5C06\u9519\u8BEF\u4F20\u9012\u51FA\u53BB\u3002</p><p>\u5F53\u7136\uFF0C\u628A\u7ED3\u679C/\u9519\u8BEF\u4F20\u9012\u51FA\u6765\u540E\u80AF\u5B9A\u8981\u505A\u540E\u7EED\u7684\u903B\u8F91\u5904\u7406\uFF0CPromise \u63D0\u4F9B\u4E86\u94FE\u5F0F\u8C03\u7528\u7684 then \u65B9\u6CD5\u3002</p><h2 id="promise-then" tabindex="-1">Promise.then() <a class="header-anchor" href="#promise-then" aria-hidden="true">#</a></h2><p>\u63A5\u7740\u4E0A\u9762\u521B\u5EFA\u7684 Promise \u5B9E\u4F8B\uFF1A</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">Promise.then(function(value) {</span></span>
<span class="line"><span style="color:#A6ACCD;">  // success</span></span>
<span class="line"><span style="color:#A6ACCD;">}, function(error) {</span></span>
<span class="line"><span style="color:#A6ACCD;">  // failure</span></span>
<span class="line"><span style="color:#A6ACCD;">});</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>then \u65B9\u6CD5\u63A5\u6536\u4E24\u4E2A\u56DE\u8C03\u51FD\u6570\u4F5C\u4E3A\u53C2\u6570\uFF0C\u7B2C\u4E00\u4E2A\u662F\u6210\u529F\u65F6\u8C03\u7528\uFF0C\u7B2C\u4E8C\u4E2A\u662F\u5931\u8D25\u65F6\u8C03\u7528\u3002</p><p>\u4E3E\u4E00\u4E2A\u5B9A\u65F6\u5668\u7684\u4F8B\u5B50\uFF1A</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">function timeout(ms){</span></span>
<span class="line"><span style="color:#A6ACCD;">    return new Promise((resolve,reject)=&gt;{</span></span>
<span class="line"><span style="color:#A6ACCD;">        setTimeout(resolve,ms,&#39;done&#39;)</span></span>
<span class="line"><span style="color:#A6ACCD;">    })</span></span>
<span class="line"><span style="color:#A6ACCD;">}</span></span>
<span class="line"><span style="color:#A6ACCD;">timeouut(100).then(res=&gt;{</span></span>
<span class="line"><span style="color:#A6ACCD;">    console.log(res);</span></span>
<span class="line"><span style="color:#A6ACCD;">})</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>timeout \u8FD4\u56DE\u4E86\u4E00\u4E2A Promise \u5B9E\u4F8B\uFF0C\u8868\u793A\u4E00\u6BB5\u65F6\u95F4\u540E\u53D1\u751F\u7ED3\u679C\uFF0C\u8FC7\u4E86\u4E00\u6BB5\u65F6\u95F4\u540E\uFF0CPromise \u7684\u5B9E\u4F8B\u72B6\u6001\u53D8\u6210 Resolved\uFF0C\u89E6\u53D1 then \u65B9\u6CD5\u7ED1\u5B9A\u7684\u56DE\u8C03\u51FD\u6570\u3002</p><h2 id="promise-catch" tabindex="-1">Promise.catch() <a class="header-anchor" href="#promise-catch" aria-hidden="true">#</a></h2><p>\u94FE\u5F0F\u8C03\u7528\u9664\u4E86 then\uFF0C\u8FD8\u6709 catch \u65B9\u6CD5\u7528\u4E8E\u6355\u83B7\u9519\u8BEF\u3002 \u4E3E\u4E2A\u4F8B\u5B50\uFF1A</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">let Promise = new Promise(function(resolve, reject) {</span></span>
<span class="line"><span style="color:#A6ACCD;">  throw new Error(&#39;test&#39;);</span></span>
<span class="line"><span style="color:#A6ACCD;">});</span></span>
<span class="line"><span style="color:#A6ACCD;">Promise.catch(function(error) {</span></span>
<span class="line"><span style="color:#A6ACCD;">  console.log(error);</span></span>
<span class="line"><span style="color:#A6ACCD;">});</span></span>
<span class="line"><span style="color:#A6ACCD;">// Error: test</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>\u5982\u679C Promise \u7684\u72B6\u6001\u5DF2\u7ECF\u53D8\u6210 Resolved\uFF0C\u90A3\u4E48\u518D\u629B\u51FA\u9519\u8BEF\u662F\u65E0\u6548\u7684\u3002 \u4F8B\u5982\uFF1A</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">let Promise = new Promise(function(resolve, reject) {</span></span>
<span class="line"><span style="color:#A6ACCD;">  resolve(1)</span></span>
<span class="line"><span style="color:#A6ACCD;">  throw new Error(&#39;test&#39;);</span></span>
<span class="line"><span style="color:#A6ACCD;">});</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">Promise.then(res=&gt;{</span></span>
<span class="line"><span style="color:#A6ACCD;">    console.log(res);</span></span>
<span class="line"><span style="color:#A6ACCD;">})</span></span>
<span class="line"><span style="color:#A6ACCD;">Promise.catch(function(error) {</span></span>
<span class="line"><span style="color:#A6ACCD;">  console.log(error);</span></span>
<span class="line"><span style="color:#A6ACCD;">});</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>\u5728 resolve \u8BED\u53E5\u4E4B\u540E\u629B\u51FA\u9519\u8BEF\uFF0C\u4E0D\u4F1A\u88AB\u6355\u83B7\u3002</p><h3 id="\u5EFA\u8BAE" tabindex="-1">\u5EFA\u8BAE <a class="header-anchor" href="#\u5EFA\u8BAE" aria-hidden="true">#</a></h3><p>then \u65B9\u6CD5\u7684\u7B2C\u4E8C\u4E2A\u53C2\u6570\u53EF\u4EE5\u5B9A\u4E49 Rejct \u72B6\u6001\u7684\u56DE\u8C03\u51FD\u6570\uFF0C\u4F46\u662F\u5EFA\u8BAE\u603B\u662F\u7528 catch \u65B9\u6CD5\u6355\u83B7\u9519\u8BEF\u3002</p><p><strong>Promise \u7684\u9519\u8BEF\u5177\u6709\u5192\u6CE1\u6027\u8D28\uFF0C\u4F1A\u4E00\u76F4\u5411\u540E\u4F20\u9012\uFF0C\u76F4\u5230\u88AB\u6355\u83B7</strong></p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">// bad</span></span>
<span class="line"><span style="color:#A6ACCD;">Promise</span></span>
<span class="line"><span style="color:#A6ACCD;">  .then(function(data) {</span></span>
<span class="line"><span style="color:#A6ACCD;">    // success</span></span>
<span class="line"><span style="color:#A6ACCD;">  }, function(err) {</span></span>
<span class="line"><span style="color:#A6ACCD;">    // error</span></span>
<span class="line"><span style="color:#A6ACCD;">  });</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">// good</span></span>
<span class="line"><span style="color:#A6ACCD;">Promise</span></span>
<span class="line"><span style="color:#A6ACCD;">  .then(function(data) { //cb</span></span>
<span class="line"><span style="color:#A6ACCD;">    // success</span></span>
<span class="line"><span style="color:#A6ACCD;">  })</span></span>
<span class="line"><span style="color:#A6ACCD;">  .catch(function(err) {</span></span>
<span class="line"><span style="color:#A6ACCD;">    // error</span></span>
<span class="line"><span style="color:#A6ACCD;">  });</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>\u6B64\u5916\uFF0Ccatch \u65B9\u6CD5\u8FD4\u56DE\u7684\u8FD8\u662F\u4E00\u4E2A Promise \u5BF9\u8C61\uFF0C\u56E0\u6B64\u8FD8\u53EF\u4EE5\u63A5\u7740\u8C03\u7528 then \u65B9\u6CD5\uFF1A</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">Promise.resolve()</span></span>
<span class="line"><span style="color:#A6ACCD;">.catch(function(error) {</span></span>
<span class="line"><span style="color:#A6ACCD;">  console.log(&#39;error&#39;, error);</span></span>
<span class="line"><span style="color:#A6ACCD;">})</span></span>
<span class="line"><span style="color:#A6ACCD;">.then(function() {</span></span>
<span class="line"><span style="color:#A6ACCD;">  console.log(&#39;gogogo&#39;);</span></span>
<span class="line"><span style="color:#A6ACCD;">});</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;"> // gogogo</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>\u8FD9\u91CC\u5E76\u6CA1\u6709\u6355\u83B7\u5230\u9519\u8BEF\uFF0C\u56E0\u6B64 then \u4F1A\u76F4\u63A5\u6267\u884C\u3002\u4F46\u662F\u5982\u679C\u540E\u7EED\u7684 then \u91CC\u9762\u62A5\u9519\uFF0C\u90A3\u4E48 catch \u65B9\u6CD5\u5219\u65E0\u6CD5\u6355\u83B7\u3002</p><h2 id="promise-finally" tabindex="-1">Promise.finally() <a class="header-anchor" href="#promise-finally" aria-hidden="true">#</a></h2><p>\u548C try catch \u4E2D\u7684 finally \u5B57\u53E5\u4E00\u6837\uFF0Cpromise \u4E5F\u6709 finally\uFF0C\u8C03\u7528.finally(f)\u5C31\u7C7B\u4F3C\u4E8E.then(f,f)\uFF0C\u65E0\u8BBA promise \u88AB resolve \u8FD8\u662F reject\uFF0Cf \u90FD\u4F1A\u6267\u884C\u3002</p><p>\u4E3E\u4F8B\uFF0C\u5F53\u6211\u4EEC ajax \u8BF7\u6C42\u65E0\u8BBA\u5931\u8D25\u6216\u8005\u6210\u529F\uFF0Cloading \u5E94\u8BE5\u5173\u95ED</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">   new Promise((resolve,reject)=&gt;{</span></span>
<span class="line"><span style="color:#A6ACCD;">    if(...){</span></span>
<span class="line"><span style="color:#A6ACCD;">        resolve(value)</span></span>
<span class="line"><span style="color:#A6ACCD;">    }else{</span></span>
<span class="line"><span style="color:#A6ACCD;">        reject(error)</span></span>
<span class="line"><span style="color:#A6ACCD;">    }</span></span>
<span class="line"><span style="color:#A6ACCD;">   }).finally(()=&gt;{</span></span>
<span class="line"><span style="color:#A6ACCD;">        loading = false;</span></span>
<span class="line"><span style="color:#A6ACCD;">   })</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p><strong>\u6CE8\u610F</strong></p><ol><li>\u867D\u7136\u7C7B\u4F3C\u4E8E.then(f,f)\uFF0C\u4F46\u662F finally \u662F\u6CA1\u6709\u53C2\u6570\u7684\uFF0Cfinally \u5185\u90E8\u4E0D\u77E5\u9053 promise \u662F\u5426\u6210\u529F\uFF0C\u56E0\u6B64\u8FD9\u91CC\u9762\u6267\u884C\u7684\u4EFB\u52A1\u901A\u5E38\u662F\u201C\u5E38\u89C4\u201D\u7684\u5B8C\u6210\u7A0B\u5E8F\u3002</li></ol><p>2.finally \u5E76\u4E0D\u610F\u5473\u7740\u5904\u7406\u4E00\u4E2A promise \u7684\u7ED3\u679C\uFF0C\u56E0\u6B64\u5B83\u53EF\u4EE5\u7EE7\u7EED\u4F20\u9012\u7ED9 then\u3001catch \u65B9\u6CD5\u5904\u7406\u3002</p><h2 id="promise-\u9759\u6001\u65B9\u6CD5" tabindex="-1">Promise \u9759\u6001\u65B9\u6CD5 <a class="header-anchor" href="#promise-\u9759\u6001\u65B9\u6CD5" aria-hidden="true">#</a></h2><ul><li><h3 id="promise-all" tabindex="-1">Promise.all() <a class="header-anchor" href="#promise-all" aria-hidden="true">#</a></h3></li></ul><p>\u5F53\u6709\u591A\u4E2A Promise \u5BF9\u8C61\u7684\u65F6\u5019\uFF0C\u53EF\u4EE5\u901A\u8FC7 Promise.all \u5C06\u591A\u4E2A\u5B9E\u4F8B\u5305\u88C5\u6210\u4E00\u4E2A\u65B0\u7684 Promise \u5B9E\u4F8B\uFF0C\u8FD9\u662F Promise \u7684\u9759\u6001\u65B9\u6CD5\u3002</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">   let p = Promise.all([p1,p2,p3])</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>Promise.all \u63A5\u6536\u4E00\u4E2A\u6570\u7EC4\uFF0C\u6BCF\u4E00\u4E2A\u90FD\u662F Promise \u5BF9\u8C61\u7684\u5B9E\u4F8B\u3002</p><p>p \u7684\u72B6\u6001\u7531 p1,p2,p3 \u5171\u540C\u51B3\u5B9A\uFF0C\u5206\u6210\u4E24\u79CD\u60C5\u51B5\uFF1A</p><ul><li>\uFF081\uFF09\u53EA\u6709\u4E09\u8005\u72B6\u6001\u90FD\u53D8\u6210 fulfilled\uFF0Cp \u7684\u72B6\u6001\u624D\u4F1A\u53D8\u6210 fulfilled\uFF0C\u6B64\u65F6\uFF0C\u4E09\u8005\u7684\u8FD4\u56DE\u503C\u7EC4\u6210\u4E00\u4E2A\u6570\u7EC4\u8FD4\u56DE\u3002</li><li>\uFF082\uFF09\u4E09\u8005\u4E2D\u4EFB\u610F\u4E00\u4E2A rejected\uFF0C\u5219 p \u7684\u72B6\u6001\u53D8\u6210 rejected\uFF0C\u7B2C\u4E00\u4E2A\u88AB rejected \u7684\u5B9E\u4F8B\u8FD4\u56DE\u503C\u8FD4\u56DE\u3002</li></ul><p>\u8FD9\u4E2A\u5728\u6211\u4EEC\u5E73\u65F6\u5F00\u53D1\u4E2D\u5E38\u7528\uFF0C\u4F8B\u5982\u4E00\u4E2A\u9875\u9762\u4E0A\u9700\u8981\u67E5\u8BE2\u591A\u4E2A\u63A5\u53E3\uFF0C\u5E76\u4E14\u53E6\u5916\u4E00\u4E2A\u63A5\u53E3\u4F9D\u8D56\u4E8E\u8FD9\u4E9B\u63A5\u53E3\u7684\u8FD4\u56DE\u503C\uFF0C\u4E5F\u5C31\u662F\u8BF4\u9700\u8981\u5728\u8FD9\u4E9B\u63A5\u53E3\u5168\u90E8\u8FD4\u56DE\u540E\u624D\u80FD\u8C03\u53E6\u5916\u4E00\u4E2A\u63A5\u53E3\uFF0C\u8FD9\u65F6\u5C31\u53EF\u4EE5\u7528 Promise.all \u6765\u89E3\u51B3\u3002</p><ul><li><h3 id="promise-race" tabindex="-1">Promise.race() <a class="header-anchor" href="#promise-race" aria-hidden="true">#</a></h3></li></ul><p>\u540C\u6837\uFF0CPromise.race \u65B9\u6CD5\u4E5F\u7528\u4E8E\u591A\u4E2A Promise \u5B9E\u4F8B\u7684\u573A\u666F\uFF0C</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">   let p = Promise.race([p1,p2,p3])</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>\u4E0E Promise.all \u4E0D\u540C\uFF0C\u5F53 p1\uFF0Cp2\uFF0Cp3 \u4E4B\u4E2D\u53C8\u4E00\u4E2A\u7387\u5148\u6539\u53D8\u72B6\u6001\uFF0Cp \u7684\u72B6\u6001\u5C31\u4F1A\u8DDF\u7740\u6539\u53D8\uFF0C\u90A3\u4E2A\u7387\u5148\u6539\u53D8\u7684\u5B9E\u4F8B\u8FD4\u56DE\u503C\uFF0C\u5C31\u4F1A\u4F20\u7ED9 p \u7684\u56DE\u8C03\u51FD\u6570\u3002</p><ul><li><h3 id="promise-resolve" tabindex="-1">Promise.resolve() <a class="header-anchor" href="#promise-resolve" aria-hidden="true">#</a></h3></li></ul><p>\u5F53\u6211\u4EEC\u9700\u8981\u5C06\u73B0\u6709\u5BF9\u8C61\u8F6C\u5316\u6210 Promise \u5BF9\u8C61\u65F6\uFF0C\u53EF\u4EE5\u7528\u5230\u6B64\u65B9\u6CD5\u3002\u6B64\u65B9\u6CD5\u7B49\u4EF7\u4E8E\u5982\u4E0B\u5199\u6CD5\uFF1A</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">Promise.resolve(&#39;&#39;)</span></span>
<span class="line"><span style="color:#A6ACCD;">// \u7B49\u4EF7\u4E8E</span></span>
<span class="line"><span style="color:#A6ACCD;">new Promise(resolve=&gt;resolve(&#39;&#39;))</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>\u6B64\u5916\uFF0C\u6B64\u65B9\u6CD5\u6709\u56DB\u79CD\u7C7B\u578B\u53C2\u6570\uFF1A</p><p>\uFF081\uFF09Promise \u5B9E\u4F8B\u3002\u90A3\u4E48\u5C06\u539F\u5C01\u4E0D\u52A8\u8FD4\u56DE\u8FD9\u4E2A\u5B9E\u4F8B</p><p>\uFF082\uFF09\u5177\u6709 then \u65B9\u6CD5\u7684\u5BF9\u8C61(\u5E76\u6CA1\u6709\u5B9E\u9645\u610F\u4E49)</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">let thenable = {</span></span>
<span class="line"><span style="color:#A6ACCD;">  then: function(resolve, reject) {</span></span>
<span class="line"><span style="color:#A6ACCD;">    resolve(42);</span></span>
<span class="line"><span style="color:#A6ACCD;">  }</span></span>
<span class="line"><span style="color:#A6ACCD;">};</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">let p1 = Promise.resolve(thenable);</span></span>
<span class="line"><span style="color:#A6ACCD;">p1.then(function(value) {</span></span>
<span class="line"><span style="color:#A6ACCD;">  console.log(value);  // 42</span></span>
<span class="line"><span style="color:#A6ACCD;">});</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>\uFF083\uFF09\u539F\u59CB\u503C/\u4E0D\u5177\u6709 then \u65B9\u6CD5\u7684\u5BF9\u8C61</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">var p = Promise.resolve(&#39;Hello&#39;);</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">p.then(function (s){</span></span>
<span class="line"><span style="color:#A6ACCD;">  console.log(s)</span></span>
<span class="line"><span style="color:#A6ACCD;">});</span></span>
<span class="line"><span style="color:#A6ACCD;">// Hello</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>\u975E\u5F02\u6B65\u64CD\u4F5C\uFF0C\u7ACB\u5373\u8FD4\u56DE\u539F\u59CB\u503C\u7ED9\u56DE\u8C03\u51FD\u6570</p><p>\uFF084\uFF09\u65E0\u53C2\u6570</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">var p = Promise.resolve();</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">p.then(function () {</span></span>
<span class="line"><span style="color:#A6ACCD;">  // ...</span></span>
<span class="line"><span style="color:#A6ACCD;">});</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>\u6700\u7B80\u5355\u7684\u65B9\u6CD5\u521B\u5EFA\u4E00\u4E2A Resolved \u72B6\u6001\u7684 Promise \u5BF9\u8C61\uFF0C\u975E\u5E38\u5E38\u7528\u3002\u4F46\u662F\u8981\u6CE8\u610F<a href="https://suprememonster.github.io/articles/javascript/EventLoop.html" target="_blank" rel="noreferrer">EventLoop</a>,\u6267\u884C\u65F6\u673A\u9700\u8981\u81EA\u884C\u628A\u63E1\u3002\u4F8B\u5982\u5E38\u89C1\u9762\u8BD5\u9898\uFF1A</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">setTimeout(function () {</span></span>
<span class="line"><span style="color:#A6ACCD;">  console.log(&#39;three&#39;);</span></span>
<span class="line"><span style="color:#A6ACCD;">}, 0);</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">Promise.resolve().then(function () {</span></span>
<span class="line"><span style="color:#A6ACCD;">  console.log(&#39;two&#39;);</span></span>
<span class="line"><span style="color:#A6ACCD;">});</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">console.log(&#39;one&#39;);</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>setTimeout(fn, 0)\u5728\u4E0B\u4E00\u8F6E\u201C\u4E8B\u4EF6\u5FAA\u73AF\u201D\u5F00\u59CB\u65F6\u6267\u884C\uFF0CPromise.resolve()\u5728\u672C\u8F6E\u201C\u4E8B\u4EF6\u5FAA\u73AF\u201D\u7ED3\u675F\u65F6\u6267\u884C\uFF0Cconsole.log(\u2019one\u2018)\u5219\u662F\u7ACB\u5373\u6267\u884C\uFF0C\u56E0\u6B64\u6700\u5148\u8F93\u51FA\u3002</p><ul><li><h3 id="promise-reject" tabindex="-1">Promise.reject() <a class="header-anchor" href="#promise-reject" aria-hidden="true">#</a></h3><p>\u548C resolve \u5BF9\u5E94\uFF0C\u57FA\u672C\u7528\u6CD5\u4E00\u81F4</p></li></ul><h2 id="promise-\u5B9E\u8DF5" tabindex="-1">Promise \u5B9E\u8DF5 <a class="header-anchor" href="#promise-\u5B9E\u8DF5" aria-hidden="true">#</a></h2><ul><li><h3 id="\u56FE\u7247\u52A0\u8F7D" tabindex="-1">\u56FE\u7247\u52A0\u8F7D <a class="header-anchor" href="#\u56FE\u7247\u52A0\u8F7D" aria-hidden="true">#</a></h3></li></ul><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">  const loadImg  = function(path){</span></span>
<span class="line"><span style="color:#A6ACCD;">    return new Promise((resolve,reject)=&gt;{</span></span>
<span class="line"><span style="color:#A6ACCD;">        let image = new Image();</span></span>
<span class="line"><span style="color:#A6ACCD;">        image.onload = resolve();</span></span>
<span class="line"><span style="color:#A6ACCD;">        image.onerror = reject();</span></span>
<span class="line"><span style="color:#A6ACCD;">        image.src = path;</span></span>
<span class="line"><span style="color:#A6ACCD;">    })</span></span>
<span class="line"><span style="color:#A6ACCD;">  }</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div>`,71),o=[p];function r(c,i,t,A,C,d){return a(),n("div",null,o)}const y=s(l,[["render",r]]);export{m as __pageData,y as default};
